import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button, Container } from 'react-bootstrap';

const ReportEmergency = () => {
  const [type, setType] = useState('');
  const [description, setDescription] = useState('');
  const [location, setLocation] = useState('');

  const emergencyTypes = [
    'Heart Attack', 'Stroke', 'Severe Allergic Reaction (Anaphylaxis)',
    'Choking', 'Seizure', 'Severe Bleeding', 'Poisoning', 'Severe Burns',
    'Earthquake', 'Flood', 'Fire', 'Tornado', 'Hurricane',
    'Road Traffic Accident', 'Fall', 'Drowning', 'Electrocution',
    'Epidemic Outbreak', 'Pandemic', 'Chemical Spill'
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://127.0.0.1:8000/emergencies/', { type, description, location });
      alert('Emergency reported successfully');
    } catch (error) {
      console.error('Error reporting emergency', error);
    }
  };

  return (
    <Container className="mt-5">
      <h2>Report Emergency</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formType">
          <Form.Label>Emergency Type</Form.Label>
          <Form.Control as="select" value={type} onChange={(e) => setType(e.target.value)} required>
            <option value="">Select Emergency Type</option>
            {emergencyTypes.map((type, index) => (
              <option key={index} value={type}>{type}</option>
            ))}
          </Form.Control>
        </Form.Group>
        <Form.Group controlId="formDescription">
          <Form.Label>Description</Form.Label>
          <Form.Control as="textarea" rows={3} value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Enter description" required />
        </Form.Group>
        <Form.Group controlId="formLocation">
          <Form.Label>Location</Form.Label>
          <Form.Control type="text" value={location} onChange={(e) => setLocation(e.target.value)} placeholder="Enter location" required />
        </Form.Group>
        <Button variant="danger" type="submit">
          Report Emergency
        </Button>
      </Form>
    </Container>
  );
};

export default ReportEmergency;
